import React,{useState} from 'react';
import Form2 from './Form2';
import Titles from './Titles';
import Weather from './Weather'

function Fiveday(){
    const [weather,setWeather] = useState([])
    const APIKEY = '9e589ccb0b9bf125c2f2a918fd3d5fca'

    function fetchData(e) {
    const city = e.target.elements.city.value
        e.preventDefault()
    const apiData =  fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${APIKEY}`)
        .then( res => res.json())
        .then(data => data) 
        .then((apidata)=>{
            console.log(apidata)
            if(city) {
                setWeather({
                    data: apidata,
                    city: apidata.city,
                    description: apidata.weather[0].description,
                    temperature: kToF(apidata.main.temp),
                    error:""
                })
            }
            else{
                setWeather({
                    data: '',
                    city: '',
                    description: '',
                    temperature: '',
                    error: "Please Input City"
                })
            }
        })

    }
    return(
        <div>
            <Titles />
            <Form2 getWeather={fetchdata}/>
            <Weather
                city={weather.city}
                description={weather.description}
                temperature={weather.temperature}
                error={weather.error}
      />
        </div>
    )  
        function kToF(kelvin){ 
            console.log(kelvin)
            const temp_f = kelvin * 9/5 - 459.67
            return +temp_f.toFixed(2)



        }

}
export default Fiveday;
