import React,{useState} from 'react';
import Form from './Form2'

function Week(){
    const [weather,setWeather] = useState([])
    const APIKEY = '9e589ccb0b9bf125c2f2a918fd3d5fca'
    let list = []
    function fetchData(e) {
        const city = e.target.elements.city.value
          e.preventDefault()
        const apiData =  fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${APIKEY}`)
          .then( res => res.json())
          .then(data => data) 
          .then((apidata)=>{
            list = apidata.list

        })  



   }
   console.log(list)
   const week = []

   list.forEach((day)=> {
    console.log("test")
       //grab current date object
        const time = day.dt
        const currentDate = new Date(time)
        //checking date object for the hours
        if(currentDate.getHours === 3){
            //if it is push to the week array
            week.push(day)

        }
   });



    return(
        <div>
            <h1> Hello</h1>
                <Form getWeather={fetchData} />
                    <Weather
                        city={weather.city}
                        description={weather.description}
                        temperature={weather.temperature}
                        error={weather.error}
                    /> 
            {console.log(weather.data)}
        </div>
    );

    function kToF(kelvin){ 
      console.log(kelvin)
      const temp_f = kelvin * 9/5 - 459.67
      return +temp_f.toFixed(2)
    }


}
export default Week;
